INSERT INTO `song` VALUES ('169', '老街', '11', 'http://localhost:8081/file/music/李荣浩 - 老街.mp3', '55', '[00:00.89]李荣浩-老街\r\n[00:03.29]作词：李荣浩\r\n[00:06.49]作曲：李荣浩\r\n[00:13.94]\r\n[00:16.89]一张褪色的照片\r\n[00:19.87]好像带给我一点点怀念\r\n[00:23.86]巷尾老爷爷卖的热汤面\r\n[00:27.91]味道弥漫过旧旧的后院\r\n[00:31.90]流浪猫睡熟在摇晃秋千\r\n[00:35.80]夕阳照了一遍他咪着眼\r\n[00:40.14]那张同桌寄的明信片\r\n[00:43.78]安静的躺在课桌的里面\r\n[00:48.69]快要过完的春天\r\n[00:51.78]还有雕刻着图案的门帘\r\n[00:55.73]窄窄的长长的过道两边\r\n[00:59.67]老房子依然升起了炊烟\r\n[01:03.66]刚刚下完了小雨的季节\r\n[01:07.65]爸妈又一起走过的老街\r\n[01:11.95]记不得哪年的哪一天\r\n[01:15.64]很漫长又很短暂的岁月\r\n[01:19.94]现在已经回不去\r\n[01:23.93]早已流逝的光阴\r\n[01:27.73]手里的那一张渐渐模糊不清的车票\r\n[01:32.74]成了回忆的信号\r\n[01:37.49]忘不掉的是什么我也不知道\r\n[01:41.78]想不起当年模样\r\n[01:45.72]看也看不到 去也去不了的地方\r\n[01:53.55]也许那老街的腔调是属于我的忧伤\r\n[02:01.81]嘴角那点微笑越来越勉强\r\n[02:09.45]忘不掉的是什么我也不知道\r\n[02:13.80]放不下熟悉片段\r\n[02:17.59]回头望一眼 已经很多年的时间\r\n[02:25.22]透过手指间看着天\r\n[02:29.62]我又回到那老街\r\n[02:33.57]靠在你们身边渐行渐远\r\n[02:40.20]\r\n[03:10.97]快要过完的春天\r\n[03:13.80]还有雕刻着图案的门帘\r\n[03:17.84]窄窄的长长的过道两边\r\n[03:21.74]老房子依然升起了炊烟\r\n[03:25.79]刚刚下完了小雨的季节\r\n[03:29.82]爸妈又一起走过的老街\r\n[03:34.02]记不得哪年的哪一天\r\n[03:37.71]很漫长又很短暂的岁月\r\n[03:42.11]现在已经回不去\r\n[03:46.15]早已流逝的光阴\r\n[03:49.79]手里的那一张渐渐模糊不清的车票\r\n[03:54.80]成了回忆的信号\r\n[03:59.70]忘不掉的是什么我也不知道\r\n[04:03.90]想不起当年模样\r\n[04:07.85]看也看不到 去也去不了的地方\r\n[04:15.95]也许那老街的腔调是属于我的忧伤\r\n[04:24.34]嘴角那点微笑越来越勉强\r\n[04:31.97]忘不掉的是什么我也不知道\r\n[04:36.11]放不下熟悉片段\r\n[04:40.16]回头望一眼 已经很多年的时间\r\n[04:47.84]透过手指间看着天\r\n[04:52.15]我又回到那老街\r\n[04:56.14]靠在你们身边渐行渐远\r\n[05:02.91]\r\n[05:08.12]');
